var jqtpl = require('jqtpl'),
    utils = require('underscore'),
    recline = require('../index'),
    fs = require('fs');

exports.render = function(template, data, options) {
	 this.template = template;
	 this.data = data;
	 this.options = options;
	 	// set the view director
	 var view = recline.viewDir ?
		recline.viewDir 
	       	:"views";

	// set the template Dir
	var templateDir = recline.templateDir ? 
		recline.templateDir  
		:  recline.viewDir + "templates";

	var t = new TMPL();

	t.settings = {
		templateDir: templateDir,
		layoutTemplate: "layout.html"
	}

	return this.send(t.rendered());
}

