var i18n = require('../i18n/eng');

exports.validations = {
	"textonly" 		: 							["!\"#\$%&'\(\)\*\+,-\./:;<=>\?@\[\\]\^_`\{\|}~", i18n("Please Enter Text")],
	"email"				: 							["^.+@.+\$", i18n("Please enter a valid email address")],
	"textinput"		: 							[".+", i18n("This field cannot be empty")],
	"radios"			: 							[".+", i18n("Please select an option.")],
	"checkbox" 		:								[".+", i18n("Please select an option.")],
	"select"			: 							[".+", i18n("Please select an option.")],
	"textarea"		: 							[".+", i18n("This field cannot be empty.")]
}

exports.lookup = function(ext, defaultType) {
  defaultType = defaultType || /.+/;

  return (ext in exports.validations)
    ? exports.validations[ext]
    : defaultType;
};