exports.i18n = {
	thousands_sep       																		: ',',
	"Please enter a valid email address"								  	: '',
	"This field cannot be empty"														: '',
	"Please select an option."															: ''
};

function i18n(s){
	if (typeof(exports.i18n)!='undefined' && exports.i18n[s]) {
		return exports.i18n[s];
	}
	return s;
}

module.exports = i18n;